<?php
// Initialize cURL session
$ch = curl_init();

// Set the URL for the POST request
curl_setopt($ch, CURLOPT_URL, 'https://www.access.service.gov.uk/login/signin/creds');

// Ensure the response is returned as a string instead of outputting it directly
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);

// Set POST request to true
curl_setopt($ch, CURLOPT_POST, 1);

curl_setopt($ch, CURLOPT_CAINFO, 'misc/cacert.pem');

// Set POST fields
curl_setopt($ch, CURLOPT_POSTFIELDS, "tes=csrfToken%2Cuser_id%2Cpassword%2Cprofile%3A8242049818125186172%3AXgyBp94OAvvTJ34LWvjQco91ooupYsXJE6lTWNz91Li2D2OpBUqd%2BfpijurBNp9zvlrUNGng6WSqeHn6sCvVtg%3D%3D&csrfToken=ba2e3edb54f1c85946b35f22d4f7edc50d00d8ba-1729274686462-d367fc72abcff4553a44099b&user_id=640164572564&password=Gurdipgrewal12&");

// Set headers
curl_setopt($ch, CURLOPT_HTTPHEADER, array(
    'Content-Type: application/x-www-form-urlencoded',
    'Content-Length: ' . strlen("tes=csrfToken%2Cuser_id%2Cpassword%2Cprofile%3A8242049818125186172%3AXgyBp94OAvvTJ34LWvjQco91ooupYsXJE6lTWNz91Li2D2OpBUqd%2BfpijurBNp9zvlrUNGng6WSqeHn6sCvVtg%3D%3D&csrfToken=ba2e3edb54f1c85946b35f22d4f7edc50d00d8ba-1729274686462-d367fc72abcff4553a44099b&user_id=640164572564&password=Gurdipgrewal12&")
));

// Execute the POST request and get the response
$response = curl_exec($ch);

// Check for cURL errors
if(curl_errno($ch)) {
    echo 'Error: ' . curl_error($ch);
} else {
    // Output the response from the server
    echo $response;
}

// Close the cURL session
curl_close($ch);
?>
